            onChange={(e)=>{
                console.log(e);
                //setCoordinates({lat: e.center.lat, lng: e.center.lng});
                //setBounds({})
            }}
            onChildClick={''}
            >

            </GoogleMapReact>
        </div>
    );
}

export default Map;
